import { useState } from 'react';

function Callout() {
  const [visible, setVisible] = useState(true);

  const closeCallout = () => {
    setVisible(false);
  }

  return (
    <>
      {visible &&
        <div className="callout" data-closable>
          <button className="close-button" aria-label="Close alert" type="button" onClick={closeCallout} data-close>
            <span aria-hidden="true">&times;</span>
          </button>
          <p>Look at this close button!</p>
        </div>
      }
    </>
  );
}

export default Callout;
