import React, { useState } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope, faUser, faUnlockAlt } from "@fortawesome/free-solid-svg-icons";




function Card(props) {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [email, setEmail] = useState('');

  const handleAddClick = () => {
    if (name && age && email) {
      props.onAdd({ name, age, email });
      setName('');
      setAge('');
      setEmail('');
    }
  };

  return (


    <div className="card" >

    <label htmlFor="name">
      <FontAwesomeIcon icon={faUser} id="icon" />
     </label>
      <input
        type="text"
        placeholder="Name"
        value={name}
        onChange={(event) => setName(event.target.value)}
      />
      <input
        type="text"
        placeholder="Age"
        value={age}
        onChange={(event) => setAge(event.target.value)}
      />
      <input
        type="text"
        placeholder="Email"
        value={email}
        onChange={(event) => setEmail(event.target.value)}
      />
      <button style = {button} onClick={handleAddClick}>Add</button>
    </div>

  );
}

function App() {
  const [cards, setCards] = useState([]);

  const handleAddCard = (cardData) => {
    setCards([...cards, cardData]);
  };

  const handleDeleteCard = (index) => {
    setCards(cards.filter((_, i) => i !== index));
  };

  return (
    <div className="app">
      <Card onAdd={handleAddCard} />
      {cards.map((card, index) => (
        <div key={index} className="card">
          <div>Name: {card.name}</div>
          <div>Age: {card.age}</div>
          <div>Email: {card.email}</div>
          <button onClick={() => handleDeleteCard(index)}>x</button>
        </div>
      ))}
    </div>
  );
}

export default App;
