import React, { useState } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope, faUser, faUnlockAlt } from "@fortawesome/free-solid-svg-icons";




function Card(props) {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [email, setEmail] = useState('');

  const handleAddClick = () => {
    if (name && age && email) {
      props.onAdd({ name, age, email });
      setName('');
      setAge('');
      setEmail('');
    }
  };

  const [selectedAccount, setSelectedAccount] = useState('Personal');

  const handleAccountChange = (event) => {
    setSelectedAccount(event.target.value);
  };

  return (
    <div className="main-block">
      <h1>ADD CARD</h1>
      <form action="/">
        <hr />
        <hr />
        <label id="icon" htmlFor="name">
          <i className="fas fa-envelope"></i>
        </label>
        <input type="text" name="name" id="name" placeholder="Name" required />
        <label id="icon" htmlFor="age">
          <i className="fas fa-user"></i>
        </label>
        <input type="text" name="age" id="age" placeholder="age" required />

        <label id="icon" htmlFor="Phone No">
          <i className="fas fa-lock"></i>
        </label>
        <input type="text" name="Phone No" id="Phone No" placeholder="Phone No" required />
        <hr />

        <div className="btn-block">
          <button type="submit" href="/">Add</button>
        </div>
      </form>
    </div>
  );
}

function App() {
  const [cards, setCards] = useState([]);

  const handleAddCard = (cardData) => {
    setCards([...cards, cardData]);
  };

  const handleDeleteCard = (index) => {
    setCards(cards.filter((_, i) => i !== index));
  };

  return (
    <div className="app">
      <Card onAdd={handleAddCard} />
      {cards.map((card, index) => (
        <div key={index} className="card">
          <div>Name: {card.name}</div>
          <div>Age: {card.age}</div>
          <div>Email: {card.email}</div>
          <button onClick={() => handleDeleteCard(index)}>x</button>
        </div>
      ))}
    </div>
  );
}

export default App;
